    } var minimumCost = function(n, edges) {
    // 城市编号为 1...n，所以初始化大小为 n + 1
    let uf = UF.create(n + 1);
    // 对所有边按照权重从小到大排序
    edges.sort((a, b) => a[2] - b[2]);
    // 记录最小生成树的权重之和
    let mst = 0;
    for (let edge of edges) {
        let u = edge[0];
        let v = edge[1];
        let weight = edge[2];
        // 若这条边会产生环，则不能加入 mst
        if (uf.connected(u, v)) {
            // @visualize color *graph._e(u,v) #c3364b
            continue;
        }
        // 若这条边不会产生环，则属于最小生成树
        mst += weight;
        // @visualize color *graph._e(u,v) #167e24
        uf.union(u, v);
    }
    // 保证所有节点都被连通
    // 按理说 uf.count() == 1 说明所有节点被连通
    // 但因为节点 0 没有被使用，所以 0 会额外占用一个连通分量
    return uf.count() == 2 ? mst : -1;
};

