    } var isBipartite = function(graph) {
    // 记录图是否符合二分图性质
    let ok = true;
    // 记录图中节点的颜色，false 和 true 代表两种不同颜色
    let color = new Array(graph.size()).fill(false);
    // 记录图中节点是否被访问过
    let visited = new Array(graph.size()).fill(false);

    // DFS 遍历框架
    var traverse = function(graph, v) {
        // 如果已经确定不是二分图了，就不用浪费时间再递归遍历了
        if (!ok) return;

        let vColor = color[v];
        // @visualize color *graph._v(v) #??a??? vColor
        visited[v] = true;

        for (let w of graph.neighbors(v)) {
            // @visualize color *graph._e(v,w) #??a??? vColor
            if (!visited[w]) {
                // 相邻节点 w 没有被访问过
                // 那么应该给节点 w 涂上和节点 v 不同的颜色
                color[w] = !vColor;
                // 继续遍历 w
                traverse(graph, w);
            } else {
                // 相邻节点 w 已经被访问过
                // 根据 v 和 w 的颜色判断是否是二分图
                if (color[w] === color[v]) {
                    // 若相同，则此图不是二分图
                    ok = false;
                }
            }
            // @visualize color *graph._e(v,w) #unset
        }
        // @visualize color *graph._v(v) #unset
    };

    // 主函数，输入邻接表，判断是否是二分图
    let n = graph.size();
    // 因为图不一定是联通的，可能存在多个子图
    // 所以要把每个节点都作为起点进行一次遍历
    // 如果发现任何一个子图不是二分图，整幅图都不算二分图
    for (let v = 0; v < n; v++) {
        if (!visited[v]) {
            traverse(graph, v);
        }
    }

    return ok;
};

