    if (lists.length === 0) return null;
    // 虚拟头结点
    let dummy = new ListNode(-1);
    // @visualize color *dummy #4b4848
    let p = dummy;
    // 优先级队列，最小堆
    let pq = Heap.create([], (a, b) => a.val - b.val, (a) => a.val);
    // 将 k 个链表的头结点加入最小堆
    for (let head of lists) {
        if (head !== null) {
            pq.push(head);
        }
    }

    while (pq.size() > 0) {
        // 获取最小节点，接到结果链表中
        let node = pq.pop();
        p.next = node;
        if (node.next !== null) {
            pq.push(node.next);
        }
        // p 指针不断前进
        p = p.next;

        // 这段代码为了方便可视化
        // @visualize global
        let _temp = p.next;
        p.next = null;
    }
    return dummy.next;
};

