        data: ''
    } class PrefixSum2D {
    constructor(matrix) {
        let m = matrix.length, n = matrix[0].length;
        // preSum[i][j] 记录矩阵 [0, 0, i-1, j-1] 的元素和
        // @visualize global
        let preSum = Array.from({ length: m + 1 }, () => Array(n + 1).fill(0));
        if (m === 0 || n === 0) return;
        // 构造前缀和矩阵
        for (let i = 1; i <= m; i++) {
            for (let j = 1; j <= n; j++) {
                // @visualize color *preSum[i-1][j] *preSum[i][j-1] #9542ac
                // @visualize color *preSum[i-1][j-1] *matrix[i-1][j-1] #9542ac
                // @visualize color *preSum[i][j] #289c2c
                // 计算每个矩阵 [0, 0, i, j] 的元素和
                preSum[i][j] = preSum[i - 1][j] + preSum[i][j - 1] 
                                + matrix[i - 1][j - 1] - preSum[i - 1][j - 1];
                // @visualize color *preSum[i-1][j] *preSum[i][j-1] #unset
                // @visualize color *preSum[i-1][j-1] *matrix[i-1][j-1] #unset
                // @visualize color *preSum[i][j] #unset
            }
        }
        this.preSum = preSum;
    }

    // 计算子矩阵 [x1, y1, x2, y2] 的元素和
    // 目标矩阵之和由四个相邻矩阵运算获得
    sumRegion(x1, y1, x2, y2) {
        // @visualize color *this.preSum[x1][y1] *this.preSum[x2+1][y2+1] #7e???? x1
        return this.preSum[x2 + 1][y2 + 1] - this.preSum[x1][y2 + 1] 
            - this.preSum[x2 + 1][y1] + this.preSum[x1][y1];
    }
}

let matrix = [
    [3, 0, 1, 4, 2],
    [5, 6, 3, 2, 1],
    [1, 2, 0, 1, 5],
    [4, 1, 0, 1, 7],
    [1, 0, 3, 0, 5]
];

// @visualize hide
let sum = new PrefixSum2D(matrix);
console.log(sum.sumRegion(2, 1, 4, 3));
console.log(sum.sumRegion(1, 1, 3, 2));
 window.onerror = function (message, source, lineno, colno, error) {
        const root = document.getElementById('root');
        if (!root || !root.innerHTML.trim()) {
            console.log('window on error:', error, message)
            document.getElementById('error-message').style.display = 'block';
        }
    };

    // MutationObserver to monitor changes in #root
    const observer = new MutationObserver(function (mutations) {
        const root = document.getElementById('root');
        if (root && root.innerHTML.trim()) {
            document.getElementById('error-message').style.display = 'none';
            window.onerror = null; // Remove the window.onerror listener
            observer.disconnect(); // Stop observing
        }
    });

    // Observe changes in the child list of #root
    observer.observe(document.getElementById('root'), {
        childList: true,
        subtree: true
    });
