    } var generateMatrix = function(n) {
    // 初始化一个大小为 n * n 的矩阵，全部填充为 0
    let matrix = new Array(n).fill().map(() => new Array(n).fill(0));
    let upper_bound = 0, lower_bound = n - 1;
    let left_bound = 0, right_bound = n - 1;
    // 需要填入矩阵的数字
    let num = 1;

    while (num <= n * n) {
        if (upper_bound <= lower_bound) {
            // 在顶部从左向右遍历
            for (let j = left_bound; j <= right_bound; j++) {
                // @visualize color *matrix[upper_bound][j] #7cd930
                matrix[upper_bound][j] = num++;
            }
            // 上边界下移
            upper_bound++;
        }

        if (left_bound <= right_bound) {
            // 在右侧从上向下遍历
            for (let i = upper_bound; i <= lower_bound; i++) {
                // @visualize color *matrix[i][right_bound] #ccc53a
                matrix[i][right_bound] = num++;
            }
            // 右边界左移
            right_bound--;
        }

        if (upper_bound <= lower_bound) {
            // 在底部从右向左遍历
            for (let j = right_bound; j >= left_bound; j--) {
                // @visualize color *matrix[lower_bound][j] #30d9d1
                matrix[lower_bound][j] = num++;
            }
            // 下边界上移
            lower_bound--;
        }

        if (left_bound <= right_bound) {
            // 在左侧从下向上遍历
            for (let i = lower_bound; i >= upper_bound; i--) {
                // @visualize color *matrix[i][left_bound] #d130d9
                matrix[i][left_bound] = num++;
            }
            // 左边界右移
            left_bound++;
        }
    }

    return matrix;
};

