    } var spiralOrder = function(matrix) {
    var m = matrix.length, n = matrix[0].length;

    var upper_bound = 0, lower_bound = m - 1;
    var left_bound = 0, right_bound = n - 1;
    var res = [];

    // res.length == m * n 则遍历完整个数组
    while (res.length < m * n) {
        if (upper_bound <= lower_bound) {
            // 在顶部从左向右遍历
            for (var j = left_bound; j <= right_bound; j++) {
                // @visualize color *matrix[upper_bound][j] #7cd930
                res.push(matrix[upper_bound][j]);
            }
            // 上边界下移
            upper_bound++;
        }

        if (left_bound <= right_bound) {
            // 在右侧从上向下遍历
            for (var i = upper_bound; i <= lower_bound; i++) {
                // @visualize color *matrix[i][right_bound] #ccc53a
                res.push(matrix[i][right_bound]);
            }
            // 右边界左移
            right_bound--;
        }

        if (upper_bound <= lower_bound) {
            // 在底部从右向左遍历
            for (var j = right_bound; j >= left_bound; j--) {
                // @visualize color *matrix[lower_bound][j] #30d9d1
                res.push(matrix[lower_bound][j]);
            }
            // 下边界上移
            lower_bound--;
        }

        if (left_bound <= right_bound) {
            // 在左侧从下向上遍历
            for (var i = lower_bound; i >= upper_bound; i--) {
                // @visualize color *matrix[i][left_bound] #d130d9
                res.push(matrix[i][left_bound]);
            }
            // 左边界右移
            left_bound++;
        }
    }
    return res;
};

let matrix = [
    [1, 2, 3, 4, 5],
    [16, 17, 18, 19, 6],
    [15, 24, 25, 20, 7],
    [14, 23, 22, 21, 8],
    [13, 12, 11, 10, 9]
];
let result = spiralOrder(matrix);
 window.onerror = function (message, source, lineno, colno, error) {
        const root = document.getElementById('root');
        if (!root || !root.innerHTML.trim()) {
            console.log('window on error:', error, message)
            document.getElementById('error-message').style.display = 'block';
        }
    };

    // MutationObserver to monitor changes in #root
    const observer = new MutationObserver(function (mutations) {
        const root = document.getElementById('root');
        if (root && root.innerHTML.trim()) {
            document.getElementById('error-message').style.display = 'none';
            window.onerror = null; // Remove the window.onerror listener
            observer.disconnect(); // Stop observing
        }
    });

    // Observe changes in the child list of #root
    observer.observe(document.getElementById('root'), {
        childList: true,
        subtree: true
    });
