// 输入一个起点 start，计算从 start 到其他节点的最短距离
var dijkstra = function(start, graph) {
    // 定义：distTo[i] 的值就是起点 start 到达节点 i 的最短路径权重
    let distTo = Array(graph.size()).fill(Infinity);
    // base case，start 到 start 的最短距离就是 0
    distTo[start] = 0;
    // @visualize unbind distTo[start]

    // 优先级队列，distFromStart 较小的排在前面
    let pq = Heap.create([], (s1, s2) => s1.distFromStart - s2.distFromStart);
    // 从起点 start 开始进行 BFS
    pq.push(new State(start, 0));

    while (!pq.isEmpty()) {
        let cur = pq.pop();
        let curNodeID = cur.id;
        let curDistFromStart = cur.distFromStart;

        if (curDistFromStart > distTo[curNodeID]) {
            continue;
        }

        // 将 curNode 的相邻节点装入队列
        for (let nextNodeID of graph.neighbors(curNodeID)) {
            // @visualize color *graph._e(curNodeID,nextNodeID) #e77427
            let weight = graph.weight(curNodeID, nextNodeID);
            let distToNextNode = distTo[curNodeID] + weight;
            // 更新最短距离的备忘录
            if (distTo[nextNodeID] > distToNextNode) {
                // @visualize color *graph._v(nextNodeID) #7ed67e
                distTo[nextNodeID] = distToNextNode;
                pq.push(new State(nextNodeID, distToNextNode));
            }
        }
    }
    return distTo;
};

let graph = Graph.createDirectedGraphFromEdges([
    [0, 1, 2],
    [0, 2, 1],
    [1, 3, 3],
    [1, 4, 2],
    [2, 5, 1],
    [2, 6, 6],
    [3, 6, 4],
    [6, 0, 3],
    [3, 0, 1],
    [5, 4, 5],
]);

let start = 0;
let distTo = dijkstra(0, graph, start);
for (let i = 0; i < distTo.length; i++) {
    console.log(`${start} -> ${i} = ${distTo[i]}`);
}
 window.onerror = function (message, source, lineno, colno, error) {
        const root = document.getElementById('root');
        if (!root || !root.innerHTML.trim()) {
            console.log('window on error:', error, message)
            document.getElementById('error-message').style.display = 'block';
        }
    };

    // MutationObserver to monitor changes in #root
    const observer = new MutationObserver(function (mutations) {
        const root = document.getElementById('root');
        if (root && root.innerHTML.trim()) {
            document.getElementById('error-message').style.display = 'none';
            window.onerror = null; // Remove the window.onerror listener
            observer.disconnect(); // Stop observing
        }
    });

    // Observe changes in the child list of #root
    observer.observe(document.getElementById('root'), {
        childList: true,
        subtree: true
    });
