// 每个节点自行维护 State 类，记录从 s 走来的步数
class State {
    constructor(node, step) {
        // 当前节点 ID
        this.node = node;
        // 从起点 s 到当前节点的步数
        this.step = step;

        // 设置这个字段作为可视化节点的值
        this.val = node;
    }
}

var bfs = function(graph, s) {
    let visited = new Array(graph.size()).fill(false);
    // @visualize bfs
    let q = [new State(s, 0)];
    visited[s] = true;
    while (q.length !== 0) {
        let state = q.shift();
        let cur = state.node;
        let step = state.step;
        // @visualize color *graph._v(cur) #b????a step
        console.log("visit " + cur + " with path step " + step);
        let neighbors = graph.neighbors(cur);
        for (let i = 0; i < neighbors.length; i++) {
            let w = neighbors[i];
            // @visualize color *graph._e(cur,w) #b????a step
            if (!visited[w]) {
                q.push(new State(w, step + 1));
                visited[w] = true;
            }
        }
    }
}

// 创建有向加权图
let graph = Graph.createDirectedGraphFromAdjList([
    [[1, 3], [2, 5], [3, 2]],
    [[4, 4], [5, 1]],
    [[3, 6], [5, 2], [6, 3]],
    [[1, 7]],
    [[6, 2]],
    [[0, 4]],
    [[4, 5]],
]);

// 从节点 0 开始进行 BFS 遍历
bfs(graph, 0);
 window.onerror = function (message, source, lineno, colno, error) {
        const root = document.getElementById('root');
        if (!root || !root.innerHTML.trim()) {
            console.log('window on error:', error, message)
            document.getElementById('error-message').style.display = 'block';
        }
    };

    // MutationObserver to monitor changes in #root
    const observer = new MutationObserver(function (mutations) {
        const root = document.getElementById('root');
        if (root && root.innerHTML.trim()) {
            document.getElementById('error-message').style.display = 'none';
            window.onerror = null; // Remove the window.onerror listener
            observer.disconnect(); // Stop observing
        }
    });

    // Observe changes in the child list of #root
    observer.observe(document.getElementById('root'), {
        childList: true,
        subtree: true
    });
